    private void btnSortearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSortearActionPerformed
        // TODO add your handling code here: BOTAO SORTEAR
        
        //Inserindo valores inseridos em caixas de texto para variaveis String        
        String aluno1 = txtAluno01.getText();
        String aluno2 = txtAluno02.getText();
        String aluno3 = txtAluno03.getText();
        String aluno4 = txtAluno04.getText();
        
        //Criando uma lista com os nomes dos alunos inseridos acima
        ArrayList<String> listaAlunos = new ArrayList<>();        
        listaAlunos.add(aluno1);
        listaAlunos.add(aluno2);
        listaAlunos.add(aluno3);
        listaAlunos.add(aluno4);
        
        //Embaralhando os valores da lista acima
        Collections.shuffle(listaAlunos);
        
        //Inserindo os valores da lista (embaralhandos) em caixas de texto
        if (!listaAlunos.isEmpty()){
            txtAlunoSorteado01.setText(listaAlunos.remove(0));
            txtAlunoSorteado02.setText(listaAlunos.remove(0));
            txtAlunoSorteado03.setText(listaAlunos.remove(0));
            txtAlunoSorteado04.setText(listaAlunos.remove(0));
        }
        
    }//GEN-LAST:event_btnSortearActionPerformed

